INSERT INTO hbis.tbl_company (id, name, address, zip_code, phone, fax, contact, mobile, cover_id, cover_path, map_id, map_path, introduction, situation, organization, scope, personnel, history, create_by, create_time, update_by, update_time) VALUES ('fb8d9c48dbd3bcd8a0f16ef488905c7e', '邯郸市邯钢集团信达科技有限公司', '河北省邯郸市复兴路232号', '056015', '（0310） 2096666   6074881', '（0310）6075586', '冯先生', '15032000988', '98c4d5e8be9d45cd906f6a988faad58b', '2020-09-07/98c4d5e8be9d45cd906f6a988faad58b.png', '52304753f737408da3de5a3412788328', '2020-09-07/52304753f737408da3de5a3412788328.jpg', '邯郸市邯钢集团信达科技有限公司（简称信达科技），系河钢集团邯钢公司全资子公司，注册资本500万元，1997年成立。历经邯钢20年基础自动化的高速发展和信息化改造的曲折进程，通过引进消化吸收、联合、自主等途径，实施了涵盖钢铁冶金行业全工艺流程的自动化、信息化系统。凭借服务邯钢的经验和技术积累，信达科技面向社会持续发展，已经成长为能够提供自动化、信息化、通讯、监控等系统解决方案的高新技术企业。', '<p class="MsoListParagraph" style="margin: 0cm 0cm 0pt; line-height: 32pt; mso-char-indent-count: 0"><span style="font-size: 12pt; font-family: 仿宋; color: #505050; line-height: 32pt; mso-bidi-font-family: Arial; mso-font-kerning: 0pt"><font face="宋体">&nbsp;&nbsp;&nbsp; 邯郸市邯钢集团信达科技有限公司（简称信达科技），系河钢集团邯钢公司全资子公司，高新技术企业，中国仪器仪表行业协会现场设备集成技术（FDT）专业委员会会员，注册资本<span lang="EN-US">500</span>万元，<span lang="EN-US">1997</span>年成立。历经邯钢<span lang="EN-US">20</span>年基础自动化的高速发展和信息化改造的曲折进程，通过引进消化吸收、联合、自主等途径，实施了涵盖钢铁冶金行业全工艺流程的自动化、信息化系统。凭借服务邯钢的经验和技术积累，信达科技经过多年来的深耕细作，能够为客户提供大数据、智能制造、智慧物流、自动控制及通讯安防等系统解决方案和技术服务。产品覆盖钢铁冶金全产业链，在汽车制造、医药、发电、贸易、物流、公共服务等行业均有良好业绩。</font></span></p><p class="MsoListParagraph" style="margin: 0cm 0cm 0pt; line-height: 32pt; mso-char-indent-count: 0"><span style="font-size: 12pt; font-family: 仿宋; color: #505050; line-height: 32pt; mso-bidi-font-family: Arial; mso-font-kerning: 0pt"><font face="宋体">&nbsp;&nbsp; 信达科技拥有信息化、自动化、大数据、安全等领域专项十余项、软件著作权数十项。与多家国际、国内一流企业建立协作、合作关系，与国内知名高校、研究机构组件研发团队，正致力于物联网、人工智能、工控网络安全等领域的研发推广。</font></span></p><p style="text-align: center">&nbsp;</p>', '<div id="pagecontent"><p class="MsoListParagraph" style="line-height: 32px; margin: 0cm 0cm 0pt; mso-char-indent-count: 0"><span style="line-height: 32px; font-family: 仿宋; color: #505050; font-size: 14pt; mso-bidi-font-family: Arial; mso-font-kerning: 0pt"><font face="宋体">&nbsp;&nbsp;&nbsp; 信达科技公司设执行董事、监事，由邯钢集团委派；设经理一名，由邯钢集团推荐，执行董事聘任；设副经理、技术总监；设置综合部、财务部、采购部、项目部、技术部等部门。</font><span lang="EN-US"><o:p></o:p></span></span></p>
INSERT INTO hbis.tbl_additional (id, professional, pit, pat, pct, pnt, peg, cases, cis, cas, ccs, cms, ceg, culture, cev, cra, ctb) VALUES ('ecda38becdbb988dc7e824d95aa42f8f', '<p class="MsoNormal" style="line-height: 27pt; text-indent: 28pt; margin: 0cm 0cm 0pt; layout-grid-mode: char; mso-char-indent-count: 2.0; mso-line-height-rule: exactly"><span style="font-family: 宋体; font-size: 14pt; mso-bidi-font-weight: bold">邯郸市邯钢集团信达科技开发有限公司（简称信达科技），系邯钢集团全资子公司，注册资本<span lang="EN-US">500</span>万元。信达科技历经邯钢基础自动化的高速发展和信息化改造的曲折历程，凭借服务邯钢近<span lang="EN-US">20</span>年的经验和技术积累，通过自主、联合、引进消化吸收等方式，实施的自动化和信息化技术覆盖了冶金行业工艺流程的每一个环节，成为面向整个冶金行业，全面提供基础自动化、信息化、通讯监控解决方案的专业化技术部门。<span lang="EN-US"></span></span></p><p class="MsoNormal" style="line-height: 27pt; text-indent: 28pt; margin: 0cm 0cm 0pt; layout-grid-mode: char; mso-char-indent-count: 2.0; mso-line-height-rule: exactly"><span style="font-family: 宋体; font-size: 14pt; mso-bidi-font-weight: bold">信达科技致力于大数据、云计算、物联网、电子商务等先进技术的开发与应用，具备了丰富的经验和卓越的实施能力。并具备集团管控信息化解决方案实力。<span lang="EN-US"></span></span></p><p>&nbsp;</p>
